var App = require('./app');

App.Router.map(function() {
	this.route('index', {path: '/pbdportal/home'});
	this.resource('scientists', {path: '/pbdportal/scientists'}, function () {
		this.route('edit', { path: ':scientist_id/edit' });
	});
	//this.resource('todos', { path: '/' });
	// generated by ember-generate --scaffold
	this.resource('scientist_profiles');
	this.resource('scientist_profile', {path: '/scientist_profiles/:profile_id'});
	this.route('edit_scientist_profile', {path: '/scientist_profiles/:profile_id/edit'});
	this.route('new_scientist_profile', {path: '/scientist_profiles/new'});
	// end generated routes

});

App.Router.reopen({
	location: 'history'
});
